const News = (props) => {
  const [RSSLoading, setRSSLoading] = useState(false);
  const [feed, setFeed] = useState([]);

  useEffect(() => {
    console.log("API_URL : ", API_URL);
    console.log("did mawnt!")
    setRSSLoading(true);
    fetch('https://southpasadenan.com/feed')
      .then((response) => response.text())
      .then((responseData) => rssParser.parse(responseData))
      .then((rss) => {
        const items = helpers.deepValue(rss, 'items', []);
        if (items.length > 0) {
          setFeed(items)
        }
        setRSSLoading(false);
      })
      .catch((e) => {
        // setRSSLoading(false);
        rssParser.parse(XMLTest)
          .then((rss) => {
            const items = helpers.deepValue(rss, 'items', []);
            if (items.length > 0) {
              setFeed(items)
            }
            setRSSLoading(false);
          })
          .catch((e) => {
            setRSSLoading(false);
          });
      });


  }, []);

  useEffect(() => {
    console.log("RSSLoading : ", RSSLoading)
  }, [RSSLoading]);

  useEffect(() => {
  }, [feed]);

  const _drawer = useRef();

  const closeControlPanel = () => {
    _drawer.current.close()
  };

  const openControlPanel = () => {
    _drawer.current.open()
  };

  const FeedComp = ({res}) => {

    function getDescription(str) {
      const str1 = str.split("<p>")[1];
      return str1.substr(0, str1.indexOf("[&#8230;]"))
    }

    function getIMGURL(str) {
      const str1 = str.split('src="')[1];
      return str1.substr(0, str1.indexOf(`"`))
    }

    const description = getDescription(res.description)
    const IMGURL = getIMGURL(res.content)
    const date = moment(res.published).format('MMMM DD, YYYY');
    const articleLink = res.links[0].url;

    return (
      <Feed>
        <FeedImage source={{uri: IMGURL}}/>
        <FeedInfoContainer>
          <FeedTitle bold large>{res.title}</FeedTitle>
          <FeedDate tiny>
            <Text small color={'black'}>{`Southpasadenan.com `}</Text>
            {date}
          </FeedDate>
          <FeedDescription>{description}</FeedDescription>
          <ReadMore onPress={() => {
            Linking.openURL(articleLink);
          }}>
            <Text color={'white'} small>Read More</Text>
          </ReadMore>
        </FeedInfoContainer>
      </Feed>
    )
  };

  return (
    <Drawer
      type="static"
      content={<SideDrawer history={props.history} onPressClose={closeControlPanel}/>}
      openDrawerOffset={100}
      tweenHandler={Drawer.tweenPresets.parallax}
      ref={_drawer}
    >
      <StyledContainer>
        <Header history={props.history} onPressLogo={openControlPanel} />
        <StyledContainer>
          <Spinner visible={RSSLoading} textContent={'Loading...'} textStyle={{color: 'white'}}/>
          <StyledContent>
            {
              feed.map((res) => {
                return ( <FeedComp res={res} /> )
              })
            }
          </StyledContent>
        </StyledContainer>
      </StyledContainer>
    </Drawer>
  )
  // }
}

